class ErrorCatcher {
  static TryCatchErrorsDecoratorAsync(target, propertyKey, descriptor) {
    const fn = descriptor.value;

    return {
      async value(...arg) {
       try {
          return await fn.call(this, ...arg);
        } catch (error) {
          throw error;
        }
      }
    };

