"use strict";

var ClientError = require('./../Exceptions/ClientError');
var base64 = require('base64-js');
var sizeOf = require('image-size');
function picValid(EncodedImage) {
  try {
    var base64Regex = /^data:image\/(png|jpeg|jpg|gif);base64,([A-Za-z0-9+/]+={0,2})$/;
    if (!base64Regex.test(EncodedImage)) {
      throw new Error();
    }
    var dimensions = sizeOf(Buffer.from(EncodedImage.split(',')[1], 'base64'));
    if (dimensions.width > 2000 || dimensions.height > 2000) {
      throw new Error();
    }
  } catch (err) {
    throw new ClientError('Picture is not valid', 400);
  }
}
module.exports = picValid;
